import React, { Component } from 'react'
import {withRouter} from 'react-router-dom'
import {
    NavWrap
} from './styleCinema'

class Nav extends Component {

    handCity = (value) => {
        return this.props.history.push(value)
      }

      handleSearch = (value) => {
        return this.props.history.push(value)
      }
      

    render() {
        return (
            <>
                <NavWrap>
                    <div  onClick={this.handCity.bind(this, '/index/city')}>
                        <span>北京</span>
                        <i className="yo-ico">&#xf033;</i>
                    </div>
                    <div className="search-entry search-input" data-type="cinema"  onClick={this.handleSearch.bind(this, '/index/search')}>
                        <img src="data:image/png;base64,"  alt=""/>
                        <span>搜影院</span>
                    </div>
                </NavWrap>
            </>
        )
    }
}

export default withRouter(Nav)